import './Operation.css'
import operationBtn from '../../assets/operationBtn.png'
import operationData from '../../data/operationData'
import operationCardBtn from '../../assets/Polygon.png'
import { useState, useRef, useEffect } from 'react'

function Operation() {
	const [isOpen, setIsOpen] = useState(false)
	const [openIndexes, setOpenIndexes] = useState([])
	const cardsWrapperRef = useRef(null)

	const toggleCard = index => {
		setOpenIndexes(prev =>
			prev.includes(index) ? prev.filter(i => i !== index) : [...prev, index]
		)
	}

	const toggleBlock = () => setIsOpen(prev => !prev)

	useEffect(() => {
		const el = cardsWrapperRef.current
		if (el) {
			if (isOpen) {
				el.style.height = el.scrollHeight + 'px'
			} else {
				el.style.height = '0px'
			}
		}
	}, [isOpen])

	return (
		<div className='Operation'>
			<div className='operationTitle' onClick={toggleBlock}>
				Mission log
				<img className='operationBtn' alt='operationBtn' src={operationBtn} />
			</div>

			<div
				className={`operationCardsWrapper ${isOpen ? 'open' : ''}`}
				ref={cardsWrapperRef}
			>
				<div className='operationCards'>
					{operationData.map((operation, index) => {
						const isCardOpen = openIndexes.includes(index)
						const contentRef = useRef(null)

						useEffect(() => {
							const el = contentRef.current
							if (el) {
								if (isCardOpen) {
									el.style.height = el.scrollHeight + 'px'
								} else {
									el.style.height = '0px'
								}
							}
						}, [isCardOpen])

						return (
							<div className='operationCard' key={index}>
								<div
									className='operationCardTop'
									onClick={() => toggleCard(index)}
								>
									<div className='operationCardTitles'>
										<div className='operationCardTitle'>{operation.title}</div>
										<div className='operationCardSubtitle'>
											{operation.subtitle}
										</div>
									</div>

									<img
										className={`operationCardBtn ${
											isCardOpen ? 'rotated' : ''
										}`}
										alt='operationCardBtn'
										src={operationCardBtn}
									/>
								</div>

								<div
									ref={contentRef}
									className={`operationCardTextWrapper ${
										isCardOpen ? 'open' : ''
									}`}
								>
									<div className='operationCardText'>{operation.text}</div>
								</div>
							</div>
						)
					})}
				</div>
			</div>
		</div>
	)
}

export default Operation
